// Example JavaScript code
function greet(name) {
    return `Hello, ${name}!`;
}

const message = greet('World');
console.log(message);

# Example Python code
def calculate_fibonacci(n):
    if n <= 1:
        return n
    return calculate_fibonacci(n-1) + calculate_fibonacci(n-2)

result = calculate_fibonacci(10)
print(f"Fibonacci(10) = {result}")
